const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('test-4', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('chrome').build()
    vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })
  it('test-4', async function() {
    await driver.get("https://www.power.fi/")
    await driver.manage().window().setRect({ width: 788, height: 892 })
    {
      const element = await driver.findElement(By.css(".text-nowrap > font > font"))
      await driver.actions({ bridge: true }).moveToElement(element).perform()
    }
    {
      const element = await driver.findElement(By.CSS_SELECTOR, "body")
      await driver.actions({ bridge: true }).moveToElement(element, 0, 0).perform()
    }
    {
      const element = await driver.findElement(By.css(".pwr-icon-cart"))
      await driver.actions({ bridge: true }).moveToElement(element).perform()
    }
    await driver.findElement(By.css(".pwr-icon-cart")).click()
  })
})
